mapper.select:

select no, name, email, gender
from user
where email = #{email}


select id, email, name
from user
where no= #{no}


select no, name
from user
where id = #{id} and password = #{password}

mapper.update:

update user
	set name = #{name},



	password= #{password},



	gender= #{gender}
	where no = #{no}
